/**
 * Resume Builder Context Provider
 * Manages global state for the Resume Builder agent
 */

import React, { createContext, useContext, useState, ReactNode } from 'react';
import { ResumeData, ResumeTemplate } from '../types';

interface ResumeBuilderContextType {
  resumeData: ResumeData;
  setResumeData: (data: ResumeData) => void;
  selectedTemplate: ResumeTemplate | null;
  setSelectedTemplate: (template: ResumeTemplate | null) => void;
  updatePersonalInfo: (info: Partial<ResumeData['personalInfo']>) => void;
  updateSection: (section: keyof ResumeData, data: any) => void;
  isPreviewMode: boolean;
  setIsPreviewMode: (mode: boolean) => void;
  isSaving: boolean;
  setIsSaving: (saving: boolean) => void;
}

const ResumeBuilderContext = createContext<ResumeBuilderContextType | undefined>(undefined);

// Default resume data structure
const defaultResumeData: ResumeData = {
  personalInfo: {
    fullName: '',
    email: '',
    phone: '',
    location: '',
  },
  summary: '',
  experience: [],
  education: [],
  skills: [],
  certifications: [],
  projects: [],
};

interface ResumeBuilderProviderProps {
  children: ReactNode;
}

export function ResumeBuilderProvider({ children }: ResumeBuilderProviderProps) {
  const [resumeData, setResumeData] = useState<ResumeData>(defaultResumeData);
  const [selectedTemplate, setSelectedTemplate] = useState<ResumeTemplate | null>(null);
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const [isSaving, setIsSaving] = useState(false);

  /**
   * Update personal information section
   */
  const updatePersonalInfo = (info: Partial<ResumeData['personalInfo']>) => {
    setResumeData((prev) => ({
      ...prev,
      personalInfo: { ...prev.personalInfo, ...info },
    }));
  };

  /**
   * Update any section of the resume
   */
  const updateSection = (section: keyof ResumeData, data: any) => {
    setResumeData((prev) => ({
      ...prev,
      [section]: data,
    }));
  };

  const value = {
    resumeData,
    setResumeData,
    selectedTemplate,
    setSelectedTemplate,
    updatePersonalInfo,
    updateSection,
    isPreviewMode,
    setIsPreviewMode,
    isSaving,
    setIsSaving,
  };

  return (
    <ResumeBuilderContext.Provider value={value}>
      {children}
    </ResumeBuilderContext.Provider>
  );
}

/**
 * Custom hook to use Resume Builder context
 * Throws error if used outside provider
 */
export function useResumeBuilder() {
  const context = useContext(ResumeBuilderContext);
  if (context === undefined) {
    throw new Error('useResumeBuilder must be used within ResumeBuilderProvider');
  }
  return context;
}

/**
 * Resume Builder API Routes
 * Agent-specific API endpoints for resume operations
 */

export const RESUME_BUILDER_API = {
  // Resume CRUD operations
  SAVE_RESUME: '/api/agents/resume-builder/resumes',
  GET_RESUME: (id: string) => `/api/agents/resume-builder/resumes/${id}`,
  UPDATE_RESUME: (id: string) => `/api/agents/resume-builder/resumes/${id}`,
  DELETE_RESUME: (id: string) => `/api/agents/resume-builder/resumes/${id}`,
  LIST_RESUMES: '/api/agents/resume-builder/resumes',

  // Export operations
  EXPORT_PDF: '/api/agents/resume-builder/export/pdf',
  EXPORT_DOCX: '/api/agents/resume-builder/export/docx',

  // AI operations
  AI_GENERATE_SUMMARY: '/api/agents/resume-builder/ai/generate-summary',
  AI_IMPROVE_DESCRIPTION: '/api/agents/resume-builder/ai/improve-description',
  AI_EXTRACT_KEYWORDS: '/api/agents/resume-builder/ai/extract-keywords',
  AI_OPTIMIZE_ATS: '/api/agents/resume-builder/ai/optimize-ats',

  // Interaction history
  GET_HISTORY: '/api/agents/resume-builder/history',
  SAVE_INTERACTION: '/api/agents/resume-builder/history',

  // Templates
  GET_TEMPLATES: '/api/agents/resume-builder/templates',
  GET_TEMPLATE: (id: string) => `/api/agents/resume-builder/templates/${id}`,
} as const;

