      data: metadata,
      timestamp: new Date().toISOString(),
    },
  });
}

/**
 * Get all sessions for a user
 * GET /api/agents/interview-prep/sessions?userId={userId}
 */
router.get('/sessions', async (req, res) => {
  try {
    const userId = req.query.userId as string;

    if (!userId) {
      return res.status(400).json({ error: 'userId is required' });
    }

    const sessions = await db
      .select()
      .from(interviewSessions)
      .where(eq(interviewSessions.userId, parseInt(userId)))
      .orderBy(desc(interviewSessions.createdAt));

    res.json(sessions);
  } catch (error) {
    console.error('Error fetching sessions:', error);
    res.status(500).json({ error: 'Failed to fetch sessions' });
  }
});

/**
 * Get a single session by ID
 * GET /api/agents/interview-prep/sessions/:id?userId={userId}
 */
router.get('/sessions/:id', async (req, res) => {
  try {
    const sessionId = parseInt(req.params.id);
    const userId = req.query.userId as string;

    if (!userId) {
      return res.status(400).json({ error: 'userId is required' });
    }

    const [session] = await db
      .select()
      .from(interviewSessions)
      .where(
        and(
          eq(interviewSessions.id, sessionId),
          eq(interviewSessions.userId, parseInt(userId))
        )
      )
      .limit(1);

    if (!session) {
      return res.status(404).json({ error: 'Session not found or access denied' });
    }

    res.json(session);
  } catch (error) {
    console.error('Error fetching session:', error);
    res.status(500).json({ error: 'Failed to fetch session' });
  }
});

/**
 * Create a new session
 * POST /api/agents/interview-prep/sessions
 */
router.post('/sessions', async (req, res) => {
  try {
    const {
      userId,
      title,
      interviewType,
      difficulty,
      targetRole,
      targetCompany,
      numberOfQuestions = 5,
    } = req.body;

    if (!userId || !title || !interviewType) {
      return res.status(400).json({ error: 'userId, title, and interviewType are required' });
    }

    const [session] = await db
      .insert(interviewSessions)
      .values({
        userId: parseInt(userId),
        title,
        interviewType,
        difficulty: difficulty || null,
        targetRole: targetRole || null,
        targetCompany: targetCompany || null,
        metadata: { numberOfQuestions },
      })
      .returning();

    await trackInteraction(parseInt(userId), 'session_created', {
      sessionId: session.id,
      title: session.title,
      interviewType: session.interviewType,
    });

    res.json({ id: session.id, session });
  } catch (error) {
    console.error('Error creating session:', error);
    res.status(500).json({ error: 'Failed to create session' });
  }
});

/**
 * Update a session
 * PUT /api/agents/interview-prep/sessions/:id
 */
router.put('/sessions/:id', async (req, res) => {
  try {
    const sessionId = parseInt(req.params.id);
    const { userId, title, score, feedback, duration } = req.body;

    if (!userId) {
      return res.status(400).json({ error: 'userId is required' });
    }

    // Verify ownership
    const [existingSession] = await db
      .select()
      .from(interviewSessions)
      .where(
        and(
          eq(interviewSessions.id, sessionId),
          eq(interviewSessions.userId, parseInt(userId))
        )
      )
      .limit(1);

    if (!existingSession) {
      return res.status(404).json({ error: 'Session not found or access denied' });
    }

    const [updatedSession] = await db
      .update(interviewSessions)
      .set({
        title: title || existingSession.title,
        score: score !== undefined ? score : existingSession.score,
        feedback: feedback !== undefined ? feedback : existingSession.feedback,
        duration: duration !== undefined ? duration : existingSession.duration,
        updatedAt: new Date(),
      })
      .where(
        and(
          eq(interviewSessions.id, sessionId),
          eq(interviewSessions.userId, parseInt(userId))
        )
      )
      .returning();

    await trackInteraction(parseInt(userId), 'session_updated', {
      sessionId: updatedSession.id,
      title: updatedSession.title,
    });

    res.json({ success: true, session: updatedSession });
  } catch (error) {
    console.error('Error updating session:', error);
    res.status(500).json({ error: 'Failed to update session' });
  }
});

/**
 * Delete a session
 * DELETE /api/agents/interview-prep/sessions/:id?userId={userId}
 */
router.delete('/sessions/:id', async (req, res) => {
  try {
    const sessionId = parseInt(req.params.id);
    const userId = req.query.userId as string;

    if (!userId) {
      return res.status(400).json({ error: 'userId is required' });
    }

    await db
      .delete(interviewSessions)
      .where(
        and(
          eq(interviewSessions.id, sessionId),
          eq(interviewSessions.userId, parseInt(userId))
        )
      );

    await trackInteraction(parseInt(userId), 'session_deleted', { sessionId });

    res.json({ success: true });
  } catch (error) {
    console.error('Error deleting session:', error);
    res.status(500).json({ error: 'Failed to delete session' });
  }
});

/**
 * Get questions for a session
 * GET /api/agents/interview-prep/sessions/:sessionId/questions
 */
router.get('/sessions/:sessionId/questions', async (req, res) => {
  try {
    const sessionId = parseInt(req.params.sessionId);

    const questions = await db
      .select()
      .from(interviewQuestions)
      .where(eq(interviewQuestions.sessionId, sessionId))
      .orderBy(interviewQuestions.createdAt);

    res.json(questions);
  } catch (error) {
    console.error('Error fetching questions:', error);
    res.status(500).json({ error: 'Failed to fetch questions' });
  }
});

/**
 * Create a question for a session
 * POST /api/agents/interview-prep/sessions/:sessionId/questions
 */
router.post('/sessions/:sessionId/questions', async (req, res) => {
  try {
    const sessionId = parseInt(req.params.sessionId);
    const { question, interviewType, difficulty, userAnswer } = req.body;

    if (!question || !interviewType) {
      return res.status(400).json({ error: 'question and interviewType are required' });
    }

    const [newQuestion] = await db
      .insert(interviewQuestions)
      .values({
        sessionId,
        question,
        interviewType,
        difficulty: difficulty || null,
        userAnswer: userAnswer || null,
      })
      .returning();

    res.json({ id: newQuestion.id, question: newQuestion });
  } catch (error) {
    console.error('Error creating question:', error);
    res.status(500).json({ error: 'Failed to create question' });
  }
});

/**
 * Update a question
 * PUT /api/agents/interview-prep/sessions/:sessionId/questions/:questionId
 */
router.put('/sessions/:sessionId/questions/:questionId', async (req, res) => {
  try {
    const questionId = parseInt(req.params.questionId);
    const { userAnswer, evaluation, timeSpent } = req.body;

    const [updatedQuestion] = await db
      .update(interviewQuestions)
      .set({
        userAnswer: userAnswer !== undefined ? userAnswer : undefined,
        evaluation: evaluation !== undefined ? evaluation : undefined,
        timeSpent: timeSpent !== undefined ? timeSpent : undefined,
        updatedAt: new Date(),
      })
      .where(eq(interviewQuestions.id, questionId))
      .returning();

    res.json({ success: true, question: updatedQuestion });
  } catch (error) {
    console.error('Error updating question:', error);
    res.status(500).json({ error: 'Failed to update question' });
  }
});

/**
 * AI: Generate questions (Mock implementation)
 * POST /api/agents/interview-prep/ai/generate-questions
 */
router.post('/ai/generate-questions', async (req, res) => {
  try {
    const { interviewType, difficulty, targetRole, count = 5 } = req.body;

    // Mock AI-generated questions
    const mockQuestions = Array.from({ length: count }, (_, i) => ({
      question: `Sample ${interviewType} question ${i + 1} for ${targetRole || 'role'}`,
      interviewType,
      difficulty: difficulty || 'medium',
    }));

    res.json(mockQuestions);
  } catch (error) {
    console.error('Error generating questions:', error);
    res.status(500).json({ error: 'Failed to generate questions' });
  }
});

/**
 * AI: Evaluate answer (Mock implementation)
 * POST /api/agents/interview-prep/ai/evaluate-answer
 */
router.post('/ai/evaluate-answer', async (req, res) => {
  try {
    const { question, answer } = req.body;

    // Mock evaluation
    const mockEvaluation = {
      score: Math.floor(Math.random() * 30) + 70,
      strengths: ['Clear communication', 'Good structure'],
      improvements: ['Add more specific examples', 'Elaborate on key points'],
      feedback: 'Good answer overall. Consider providing more concrete examples to strengthen your response.',
    };

    res.json(mockEvaluation);
  } catch (error) {
    console.error('Error evaluating answer:', error);
    res.status(500).json({ error: 'Failed to evaluate answer' });
  }
});

/**
 * Get interaction history
 * GET /api/agents/interview-prep/history?userId={userId}
 */
router.get('/history', async (req, res) => {
  try {
    const userId = req.query.userId as string;

    if (!userId) {
      return res.status(400).json({ error: 'userId is required' });
    }

    const history = await db
      .select()
      .from(agentInteractions)
      .where(
        and(
          eq(agentInteractions.userId, parseInt(userId)),
          eq(agentInteractions.agentSlug, 'interview-prep')
        )
      )
      .orderBy(desc(agentInteractions.createdAt))
      .limit(50);

    res.json(history);
  } catch (error) {
    console.error('Error fetching history:', error);
    res.status(500).json({ error: 'Failed to fetch history' });
  }
});

export default router;

shared/schema.ts.content:
/**
 * Database Schema for RecruitEdge Platform
 * Using Drizzle ORM with TypeScript for type-safe database operations
 */

import { pgTable, serial, varchar, text, timestamp, integer, jsonb, boolean, pgEnum } from 'drizzle-orm/pg-core';
import { relations } from 'drizzle-orm';

/**
 * Enum for user roles in the platform
 */
export const userRoleEnum = pgEnum('user_role', ['job_seeker', 'recruiter', 'admin']);

/**
 * Enum for AI provider types
 */
export const aiProviderEnum = pgEnum('ai_provider', ['openai', 'anthropic', 'gemini']);

/**
 * Enum for job status
 */
export const jobStatusEnum = pgEnum('job_status', ['draft', 'published', 'closed', 'archived']);

/**
 * Enum for application status
 */
export const applicationStatusEnum = pgEnum('application_status', [
  'applied',
  'screening',
  'interview_scheduled',
  'interviewing',
  'offer_sent',
  'accepted',
  'rejected',
  'withdrawn'
]);

/**
 * Enum for interview types
 */
export const interviewTypeEnum = pgEnum('interview_type', [
  'behavioral',
  'technical',
  'case_study',
  'system_design',
  'coding',
  'general'
]);

/**
 * Enum for difficulty levels
 */
export const difficultyLevelEnum = pgEnum('difficulty_level', ['easy', 'medium', 'hard']);

/**
 * Enum for skill gap priority
 */
export const priorityEnum = pgEnum('priority', ['low', 'medium', 'high', 'critical']);

/**
 * Enum for subscription plans
 */
export const subscriptionPlanEnum = pgEnum('subscription_plan', ['free', 'basic', 'professional', 'enterprise']);

/**
 * Enum for payment status
 */
export const paymentStatusEnum = pgEnum('payment_status', ['pending', 'completed', 'failed', 'refunded']);

/**
 * Enum for content flag status
 */
export const flagStatusEnum = pgEnum('flag_status', ['pending', 'reviewed', 'resolved', 'dismissed']);

/**
 * Enum for user status
 */
export const userStatusEnum = pgEnum('user_status', ['active', 'inactive', 'suspended']);

/**
 * Enum for audit event types
 */
export const auditEventTypeEnum = pgEnum('audit_event_type', [
  'user_login',
  'user_logout',
  'user_created',
  'user_updated',
  'user_deleted',
  'data_exported',
  'settings_changed',
  'payment_processed',
  'content_flagged',
  'system_alert'
]);

/**
 * Users table - stores all platform users (job seekers, recruiters, admins)
 */
export const users = pgTable('users', {
  id: serial('id').primaryKey(),
  email: varchar('email', { length: 255 }).notNull().unique(),
  name: varchar('name', { length: 255 }).notNull(),
  role: userRoleEnum('role').notNull(),
  status: userStatusEnum('status').notNull().default('active'),
  googleId: varchar('google_id', { length: 255 }).unique(),
  avatarUrl: text('avatar_url'),
  lastLogin: timestamp('last_login'),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * User profiles - extended information for users
 */
export const userProfiles = pgTable('user_profiles', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull().unique(),
  bio: text('bio'),
  skills: jsonb('skills').$type<string[]>(),
  experience: jsonb('experience').$type<any[]>(),
  education: jsonb('education').$type<any[]>(),
  resumeUrl: text('resume_url'),
  portfolioUrl: text('portfolio_url'),
  preferences: jsonb('preferences').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Jobs table - stores job postings
 */
export const jobs = pgTable('jobs', {
  id: serial('id').primaryKey(),
  recruiterId: integer('recruiter_id').references(() => users.id).notNull(),
  title: varchar('title', { length: 255 }).notNull(),
  description: text('description').notNull(),
  requirements: jsonb('requirements').$type<string[]>(),
  skills: jsonb('skills').$type<string[]>(),
  location: varchar('location', { length: 255 }),
  salary: jsonb('salary').$type<{ min?: number; max?: number; currency: string }>(),
  employmentType: varchar('employment_type', { length: 100 }),
  status: jobStatusEnum('status').notNull().default('draft'),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Applications table - stores job applications
 */
export const applications = pgTable('applications', {
  id: serial('id').primaryKey(),
  jobId: integer('job_id').references(() => jobs.id).notNull(),
  applicantId: integer('applicant_id').references(() => users.id).notNull(),
  status: applicationStatusEnum('status').notNull().default('applied'),
  coverLetter: text('cover_letter'),
  resumeUrl: text('resume_url'),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Agent interactions - stores user interactions with AI agents
 */
export const agentInteractions = pgTable('agent_interactions', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  agentSlug: varchar('agent_slug', { length: 255 }).notNull(),
  agentCategory: varchar('agent_category', { length: 100 }).notNull(),
  sessionId: varchar('session_id', { length: 255 }).notNull(),
  messages: jsonb('messages').$type<Array<{ role: string; content: string; timestamp: string }>>(),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Agent settings - stores per-agent AI provider configuration
 */
export const agentSettings = pgTable('agent_settings', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  agentSlug: varchar('agent_slug', { length: 255 }).notNull(),
  aiProvider: aiProviderEnum('ai_provider').notNull(),
  modelName: varchar('model_name', { length: 255 }).notNull(),
  temperature: integer('temperature'), // stored as integer (0-100), converted to 0.0-1.0
  maxTokens: integer('max_tokens'),
  additionalConfig: jsonb('additional_config').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Job alerts - stores user preferences for job notifications
 */
export const jobAlerts = pgTable('job_alerts', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  name: varchar('name', { length: 255 }).notNull(),
  criteria: jsonb('criteria').$type<Record<string, any>>().notNull(),
  emailEnabled: boolean('email_enabled').default(true),
  whatsappEnabled: boolean('whatsapp_enabled').default(false),
  telegramEnabled: boolean('telegram_enabled').default(false),
  frequency: varchar('frequency', { length: 50 }).default('daily'),
  lastSent: timestamp('last_sent'),
  isActive: boolean('is_active').default(true),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Notification preferences - stores user notification settings
 */
export const notificationPreferences = pgTable('notification_preferences', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull().unique(),
  email: varchar('email', { length: 255 }),
  whatsappNumber: varchar('whatsapp_number', { length: 50 }),
  telegramUsername: varchar('telegram_username', { length: 255 }),
  emailVerified: boolean('email_verified').default(false),
  whatsappVerified: boolean('whatsapp_verified').default(false),
  telegramVerified: boolean('telegram_verified').default(false),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Resumes table - stores resume data from Resume Builder agent
 */
export const resumes = pgTable('resumes', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  title: varchar('title', { length: 255 }).notNull().default('Untitled Resume'),
  personalInfo: jsonb('personal_info').$type<{
    fullName: string;
    email: string;
    phone: string;
    location?: string;
    linkedin?: string;
    github?: string;
    portfolio?: string;
  }>().notNull(),
  summary: text('summary'),
  experience: jsonb('experience').$type<Array<{
    id: string;
    company: string;
    position: string;
    location: string;
    startDate: string;
    endDate: string | null;
    isCurrent: boolean;
    description: string;
    achievements: string[];
  }>>().notNull().default([]),
  education: jsonb('education').$type<Array<{
    id: string;
    institution: string;
    degree: string;
    field: string;
    location: string;
    startDate: string;
    endDate: string | null;
    isCurrent: boolean;
    gpa?: string;
    achievements: string[];
  }>>().notNull().default([]),
  skills: jsonb('skills').$type<string[]>().notNull().default([]),
  projects: jsonb('projects').$type<Array<{
    id: string;
    name: string;
    description: string;
    technologies: string[];
    url?: string;
  }>>(),
  certifications: jsonb('certifications').$type<Array<{
    id: string;
    name: string;
    issuer: string;
    date: string;
    url?: string;
  }>>(),
  template: varchar('template', { length: 100 }).default('modern'),
  isPublic: boolean('is_public').default(false),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Interview Sessions - stores interview practice sessions from Interview Prep agent
 */
export const interviewSessions = pgTable('interview_sessions', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  title: varchar('title', { length: 255 }).notNull(),
  interviewType: interviewTypeEnum('interview_type').notNull(),
  difficulty: difficultyLevelEnum('difficulty'),
  targetRole: varchar('target_role', { length: 255 }),
  targetCompany: varchar('target_company', { length: 255 }),
  duration: integer('duration'), // in minutes
  score: integer('score'), // 0-100
  feedback: text('feedback'),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Interview Questions - stores questions and answers from Interview Prep sessions
 */
export const interviewQuestions = pgTable('interview_questions', {
  id: serial('id').primaryKey(),
  sessionId: integer('session_id').references(() => interviewSessions.id).notNull(),
  question: text('question').notNull(),
  interviewType: interviewTypeEnum('interview_type').notNull(),
  difficulty: difficultyLevelEnum('difficulty'),
  userAnswer: text('user_answer'),
  aiSuggestion: text('ai_suggestion'),
  evaluation: jsonb('evaluation').$type<{
    score: number;
    strengths: string[];
    improvements: string[];
    feedback: string;
  }>(),
  timeSpent: integer('time_spent'), // in seconds
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Skill Analyses - stores skill gap analysis results from Skill Gap Analyzer agent
 */
export const skillAnalyses = pgTable('skill_analyses', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  resumeId: integer('resume_id').references(() => resumes.id),
  targetRole: varchar('target_role', { length: 255 }).notNull(),
  targetCompany: varchar('target_company', { length: 255 }),
  jobDescription: text('job_description'),
  currentSkills: jsonb('current_skills').$type<string[]>().notNull(),
  requiredSkills: jsonb('required_skills').$type<string[]>().notNull(),
  overallScore: integer('overall_score'), // 0-100
  summary: text('summary'),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Skill Gaps - stores individual skill gaps identified in analyses
 */
export const skillGaps = pgTable('skill_gaps', {
  id: serial('id').primaryKey(),
  analysisId: integer('analysis_id').references(() => skillAnalyses.id).notNull(),
  skillName: varchar('skill_name', { length: 255 }).notNull(),
  priority: priorityEnum('priority').notNull(),
  category: varchar('category', { length: 100 }), // e.g., 'technical', 'soft_skill', 'tool', 'framework'
  currentLevel: varchar('current_level', { length: 50 }), // e.g., 'none', 'beginner', 'intermediate', 'advanced'
  requiredLevel: varchar('required_level', { length: 50 }).notNull(),
  learningResources: jsonb('learning_resources').$type<Array<{
    title: string;
    url: string;
    type: 'course' | 'article' | 'video' | 'book' | 'certification';
    provider?: string;
    duration?: string;
    cost?: string;
  }>>(),
  estimatedTime: varchar('estimated_time', { length: 100 }), // e.g., '2 weeks', '3 months'
  isCompleted: boolean('is_completed').default(false),
  completedAt: timestamp('completed_at'),
  notes: text('notes'),
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Cover Letters - stores cover letters from Cover Letter Writer agent
 */
export const coverLetters = pgTable('cover_letters', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  resumeId: integer('resume_id').references(() => resumes.id),
  jobId: integer('job_id').references(() => jobs.id),
  title: varchar('title', { length: 255 }).notNull(),
  content: text('content').notNull(),
  recipientName: varchar('recipient_name', { length: 255 }),
  recipientTitle: varchar('recipient_title', { length: 255 }),
  companyName: varchar('company_name', { length: 255 }),
  jobTitle: varchar('job_title', { length: 255 }),
  tone: varchar('tone', { length: 50 }).default('professional'), // professional, enthusiastic, creative
  metadata: jsonb('metadata').$type<Record<string, any>>(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull(),
});

/**
 * Job Matches - stores AI job matching results from Job Matcher agent
 */
export const jobMatches = pgTable('job_matches', {
  id: serial('id').primaryKey(),
  userId: integer('user_id').references(() => users.id).notNull(),
  jobId: integer('job_id').references(() => jobs.id).notNull(),
  resumeId: integer('resume_id').references(() => resumes.id),
  matchScore: integer('match_score').notNull(), // 0-100
  skillsMatch: jsonb('skills_match').$type<{
    matched: string[];
    missing: string[];
    additional: string[];
