                        <span className="text-sm font-medium text-gray-700">Disk Usage</span>
                      </div>
                      <span className="text-lg font-bold text-gray-900">{data.performance.disk.toFixed(1)}%</span>
                    </div>
                    <ProgressBar value={data.performance.disk} />
