            <CardHeader className="border-b">
              <div className="flex items-center justify-between">
                <CardTitle>Add New Candidate</CardTitle>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowAddForm(false)}
                >
                  <X className="w-4 h-4" />
