type Status = 'success' | 'failure';
type Severity = 'info' | 'warning' | 'error' | 'critical';

interface AuditLog {
  id: string;
  timestamp: string;
  userId: number;
  userName: string;
  userEmail: string;
  actionType: ActionType;
  resourceType: ResourceType;
  resourceId: string;
  ipAddress: string;
  status: Status;
  severity: Severity;
  description: string;
  userAgent?: string;
  location?: string;
}

interface AuditStats {
  totalEvents: number;
  todayEvents: number;
  failedEvents: number;
  securityAlerts: number;
  trends: {
    totalEvents: number;
    todayEvents: number;
    failedEvents: number;
    securityAlerts: number;
  };
}


