                    <option value="error">Error</option>
                    <option value="critical">Critical</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Resource Type</label>
                  <select
                    value={resourceFilter}
                    onChange={(e) => setResourceFilter(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  >
                    <option value="all">All Resources</option>
                    <option value="user">User</option>
                    <option value="job">Job</option>
                    <option value="application">Application</option>
                    <option value="resume">Resume</option>
                    <option value="system">System</option>
                    <option value="settings">Settings</option>
                    <option value="document">Document</option>
                  </select>
                </div>
              </div>
            )}

            <div className="mt-4 text-sm text-gray-600">
              Showing {filteredLogs.length} of {logs.length} events
            </div>
          </div>

          <CardContent className="p-0">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50 border-b border-gray-200">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Timestamp</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Resource</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">IP Address</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Severity</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Description</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredLogs.map((log) => {
                    const ActionIcon = getActionIcon(log.actionType);
                    const severityColors = getSeverityColor(log.severity);
                    const statusColors = getStatusColor(log.status);
                    const StatusIcon = log.status === 'success' ? CheckCircle : XCircle;

                    return (
                      <tr key={log.id} className="hover:bg-gray-50 transition">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center gap-2">
                            <Clock className="w-4 h-4 text-gray-400" />
                            <div>
                              <div className="text-sm font-medium text-gray-900">
                                {new Date(log.timestamp).toLocaleTimeString()}
                              </div>
                              <div className="text-xs text-gray-500">
                                {new Date(log.timestamp).toLocaleDateString()}
                              </div>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center gap-2">
                            <User className="w-4 h-4 text-gray-400" />
                            <div>
                              <div className="text-sm font-medium text-gray-900">{log.userName}</div>
                              <div className="text-xs text-gray-500">{log.userEmail}</div>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium border ${getActionColor(log.actionType)}`}>
                            <ActionIcon className="w-3 h-3" />
                            {log.actionType}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center gap-2">
                            <Database className="w-4 h-4 text-gray-400" />
                            <div>
                              <div className="text-sm text-gray-900">{log.resourceType}</div>
                              <div className="text-xs text-gray-500">{log.resourceId}</div>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center gap-2">
                            <Globe className="w-4 h-4 text-gray-400" />
                            <div>
                              <div className="text-sm text-gray-900 font-mono">{log.ipAddress}</div>
                              {log.location && (
                                <div className="text-xs text-gray-500">{log.location}</div>
                              )}
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium border ${statusColors.bg} ${statusColors.border} ${statusColors.text}`}>
                            <StatusIcon className="w-3 h-3" />
                            {log.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-medium border ${severityColors.bg} ${severityColors.border} ${severityColors.text}`}>
                            {log.severity === 'critical' && <AlertTriangle className="w-3 h-3" />}
                            {log.severity === 'error' && <XCircle className="w-3 h-3" />}
                            {log.severity === 'warning' && <Bell className="w-3 h-3" />}
                            {log.severity === 'info' && <Activity className="w-3 h-3" />}
                            {log.severity}
                          </span>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-900 max-w-md">{log.description}</div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            {filteredLogs.length === 0 && (
              <div className="text-center py-12">
                <FileSearch className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-1">No logs found</h3>
                <p className="text-sm text-gray-500">Try adjusting your search or filters</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

server/routes/auditLogger.ts.content:
import { Router } from 'express';
import { db } from '../db';
import { auditLogs, agentInteractions } from '@shared/schema';
import { desc, eq } from 'drizzle-orm';

const router = Router();

async function trackInteraction(userId: number, actionType: string, metadata?: any) {
  await db.insert(agentInteractions).values({
    userId,
    agentSlug: 'audit-logger',
    agentCategory: 'admin-agents',
    sessionId: `session_${Date.now()}_${userId}`,
    messages: [],
    metadata: { type: actionType, data: metadata, timestamp: new Date().toISOString() },
  });
}

router.get('/logs', async (req, res) => {
  try {
    const { userId, eventType } = req.query;
    if (!userId) return res.status(400).json({ error: 'userId is required' });

    await trackInteraction(parseInt(userId as string), 'list_logs');

    let query = db.select().from(auditLogs).orderBy(desc(auditLogs.createdAt)).limit(100);

    if (eventType) {
      query = query.where(eq(auditLogs.eventType, eventType as any));
    }

    const logs = await query;
    res.json(logs);
  } catch (error) {
    console.error('Error fetching audit logs:', error);
    res.status(500).json({ error: 'Failed to fetch audit logs' });
  }
});

router.post('/logs', async (req, res) => {
  try {
    const { userId, eventType, entityType, entityId, action, changes, ipAddress } = req.body;
    if (!eventType || !action) {
      return res.status(400).json({ error: 'eventType and action are required' });
    }

    const [log] = await db.insert(auditLogs).values({
      userId: userId ? parseInt(userId) : null,
      eventType,
      entityType: entityType || null,
      entityId: entityId ? parseInt(entityId) : null,
      action,
      changes: changes || {},
      ipAddress: ipAddress || null,
    }).returning();

    res.status(201).json(log);
  } catch (error) {
    console.error('Error creating audit log:', error);
    res.status(500).json({ error: 'Failed to create audit log' });
  }
});

router.get('/stats', async (req, res) => {
  try {
    const { userId } = req.query;
    if (!userId) return res.status(400).json({ error: 'userId is required' });

    await trackInteraction(parseInt(userId as string), 'view_stats');

    const logs = await db.select().from(auditLogs);
    const stats = {
      totalEvents: logs.length,
      todayEvents: logs.filter(l => {
        const today = new Date();
        const logDate = new Date(l.createdAt);
        return logDate.toDateString() === today.toDateString();
      }).length,
      eventTypes: {
        user_login: logs.filter(l => l.eventType === 'user_login').length,
        user_created: logs.filter(l => l.eventType === 'user_created').length,
        payment_processed: logs.filter(l => l.eventType === 'payment_processed').length,
      },
    };

    res.json(stats);
  } catch (error) {
    console.error('Error fetching stats:', error);
    res.status(500).json({ error: 'Failed to fetch stats' });
  }
});

export default router;

