}

interface AISuggestion {
  action: 'approve' | 'remove' | 'spam' | 'request_changes' | 'ban_user';
  confidence: number;
  reasoning: string;
}

interface ModerationHistory {
  id: string;
  contentId: string;
  action: string;
  moderator: string;
  timestamp: string;
  notes?: string;
}

const mockFlaggedContent: FlaggedContent[] = [
